import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import AddModal from './components/AddModal';
import EditModal from './components/EditModal';
import Header from './components/Header';
import ParcelList from './components/ParcelList';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import DetailPage from './components/DetailPage'; 
import EditPage from './components/EditPage'; 
import HomePage from './components/HomePage'; // HomePage 컴포넌트 추가
import AboutPage from './components/AboutPage'; // AboutPage 컴포넌트 추가
import ContactPage from './components/ContactPage'; // ContactPage 컴포넌트 추가

function App() {
  // 필요한 상태 변수, 함수 선언부
  const [parcels, setParcels] = useState([]);
  const [currentParcel, setCurrentParcel] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showAddModal, setShowAddModal] = useState(false);

  const [newParcel, setNewParcel] = useState({
    tracking_number: '',
    sender_name: '',
    recipient_name: '',
    recipient_address: '',
    status: '배송 준비',
    cost: ''
  });

  // 데이터 가져오기
  const fetchParcels = async () => {
    const response = await axios.get('https://67296bac6d5fa4901b6d14a6.mockapi.io/oss4-2/parcels');
    setParcels(response.data);
  };

  useEffect(() => {
    fetchParcels();
  }, []);

  // 모달 열기/닫기
  const handleShowModal = (parcel) => {
    setCurrentParcel({ ...parcel });
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleShowAddModal = () => {
    setShowAddModal(true);
  };

  const handleCloseAddModal = () => {
    setShowAddModal(false);
  };

  // 모달 입력에 반응하여 업데이트
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCurrentParcel({ ...currentParcel, [name]: value });
  };

  // 수정 서버에 저장
  const saveChanges = async () => {
    await axios.put(
      `https://67296bac6d5fa4901b6d14a6.mockapi.io/oss4-2/parcels/${currentParcel.id}`,
      currentParcel
    );
    fetchParcels();
    setShowModal(false);
  };

  // 새 데이터 추가 입력 값 관리
  const handleNewParcelChange = (e) => {
    const { name, value } = e.target;
    setNewParcel({ ...newParcel, [name]: value });
  };

  // 새 데이터 추가
  const addParcel = async () => {
    await axios.post('https://67296bac6d5fa4901b6d14a6.mockapi.io/oss4-2/parcels', newParcel);
    fetchParcels();
    setNewParcel({
      tracking_number: '',
      sender_name: '',
      recipient_name: '',
      recipient_address: '',
      status: '',
      cost: ''
    });

    setShowAddModal(false);
  };

  // 데이터 삭제
  const deleteParcel = async (id) => {
    const confirmDelete = window.confirm('정말로 삭제하시겠습니까?');
    if (confirmDelete) {
      await axios.delete(`https://67296bac6d5fa4901b6d14a6.mockapi.io/oss4-2/parcels/${id}`);
      fetchParcels();
    }
  };

  return (
    <Router>
      <div className="container">
        <Header handleShowAddModal={handleShowAddModal} />

        {/* 라우트 설정 */}
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/about" element={<AboutPage />} />
          <Route path="/contact" element={<ContactPage />} />
          <Route
            path="/parcels"
            element={
              <ParcelList parcels={parcels} handleShowModal={handleShowModal} deleteParcel={deleteParcel} />
            }
          />
          <Route path="/edit/:id" element={<EditPage />} />
          <Route path="/parcel/:id" element={<DetailPage />} />
        </Routes>

        {/* 수정 모달 */}
        {currentParcel && (
          <EditModal
            show={showModal}
            handleClose={handleCloseModal}
            handleInputChange={handleInputChange}
            saveChanges={saveChanges}
            currentParcel={currentParcel}
          />
        )}

        {/* 추가 모달 */}
        <AddModal
          show={showAddModal}
          handleClose={handleCloseAddModal}
          handleNewParcelChange={handleNewParcelChange}
          addParcel={addParcel}
          newParcel={newParcel}
        />

        {/* 수정된 푸터 */}
        <footer className="text-center mt-4">
          <div className="footer-content">
            <p>문의: contact@parcelmanagement.com | 전화: 123-456-7890</p>
            <div className="social-icons">
              <a href="https://facebook.com" target="_blank" rel="noopener noreferrer">Facebook</a> |
              <a href="https://twitter.com" target="_blank" rel="noopener noreferrer">Twitter</a> |

            </div>
            <div className="footer-nav">
              <Link to="/">Home</Link> | 
              <Link to="/about">About Us</Link> | 
              <Link to="/contact">Contact</Link> | 
              <a href="https://instagram.com" target="_blank" rel="noopener noreferrer">Instagram</a>
            </div>
            <p>© 2024 Parcel Management System. <Link to="/terms">Terms of Service</Link> | <Link to="/privacy">Privacy Policy</Link></p>
            <p>Developed by ChoJunehee and Team</p>
          </div>
        </footer>
      </div>
    </Router>
  );
}

export default App;
