// App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import AddModal from './components/AddModal';
import EditModal from './components/EditModal';
import Header from './components/Header';
import ParcelList from './components/ParcelList';
import DetailPage from './components/DetailPage';

function App() {
  const [parcels, setParcels] = useState([]);
  const [currentParcel, setCurrentParcel] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showAddModal, setShowAddModal] = useState(false);

  const fetchParcels = async () => {
    const response = await axios.get('https://67296bac6d5fa4901b6d14a6.mockapi.io/oss4-2/parcels');
    setParcels(response.data);
  };

  useEffect(() => {
    fetchParcels();
  }, []);

  const handleShowModal = (parcel) => {
    setCurrentParcel({ ...parcel });
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleShowAddModal = () => {
    setShowAddModal(true);
  };

  const handleCloseAddModal = () => {
    setShowAddModal(false);
  };

  const deleteParcel = async (id) => {
    await axios.delete(`https://67296bac6d5fa4901b6d14a6.mockapi.io/oss4-2/parcels/${id}`);
    fetchParcels();
  };

  return (
    <Router>
      <div className="App">
        <Header handleShowAddModal={handleShowAddModal} />
        <Routes>
          <Route
            path="/"
            element={
              <ParcelList
                parcels={parcels}
                handleShowModal={handleShowModal}
                deleteParcel={deleteParcel}
              />
            }
          />
          <Route path="/detail/:id" element={<DetailPage />} />
        </Routes>
        <AddModal show={showAddModal} handleClose={handleCloseAddModal} fetchParcels={fetchParcels} />
        <EditModal
          show={showModal}
          handleClose={handleCloseModal}
          currentParcel={currentParcel}
          fetchParcels={fetchParcels}
        />
      </div>
    </Router>
  );
}

export default App;
